mapper.select:



        AND func_name like concat('%', #{funcName}, '%')


        AND visible = #{visible}


order by order_num

mapper.update:
update sys_func
func_name = #{funcName}, parent_id = #{parentId}, order_num = #{orderNum}, url = #{url}, func_type = #{funcType}, visible = #{visible}, perms = #{perms}, icon = #{icon}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where func_id = #{funcId}
